body {
  padding-top: 50px;
}

header {
  background: white;
  left: 0;
  line-height: 50px;
  height: 50px;
  padding: 0 5px;
  position: absolute;
  right: 0;
  top:0;
}
/* This is the only crutial piece of css */
header.fixed { position: fixed; }

